package com.yafatek;import java.util.Arrays;import java.util.Collections;public class CovertIntToArr {	public static void main(String[] args) {		String[] stringArray = Integer.toString(132).split("");		StringBuilder convertToInt = new StringBuilder();		Arrays.sort(stringArray, Collections.reverseOrder());		for (int i = 0; i < stringArray.length - 1; i++) {			convertToInt.append(stringArray[i]);		}		Integer.parseInt(convertToInt.toString());	}}