package com.yafatek;public class FizzBuzz {	public static void main(String[] args) {		printFizzBuzz(100);	}	private static void printFizzBuzz(int limit) {		for (int i = 0; i <= limit; i++) {			if (i % 3 == 0 && i % 5 == 0) System.out.println("FizzBuzz");			if (i % 3 == 0) System.out.println("Fizz");			else if (i % 5 == 0) System.out.println("Buzz");			System.out.println(i);		}	}}