package com.yafatek;public class IntToRoman {	public static void main(String[] args) {		System.out.println("Number 6  in Roman  = " + getRomanNumber(6));		System.out.println("Number 7  in Roman  = " + getRomanNumber(7));		System.out.println("Number 70  in Roman  = " + getRomanNumber(70));		System.out.println("Number 450  in Roman  = " + getRomanNumber(450));		System.out.println(6 / 1000);		System.out.println((433 % 1000) / 100);	}	private static String getRomanNumber(int number) {		String[] thousands = {"", "M", "MM", "MMM"};		String[] hundreds = {"", "C", "CC", "CCC", "CD", "D", "DC", "DCC", "DCCC", "CM"};		String[] tens = {"", "X", "XX", "XXX", "XL", "L", "LX", "LXX", "LXXX", "XC"};		String[] units = {"", "I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX"};		return thousands[number / 1000] +				hundreds[(number % 1000) / 100] +				tens[(number % 100) / 10] +				units[number % 10];	}}