package com.yafatek;import java.util.HashMap;import java.util.Map;import java.util.logging.Logger;public class TowSums {	private static Logger logger = Logger.getLogger(TowSums.class.getName());	public static void main(String[] args) {		int[] numbers = {1, 2, 3, 4, 7, 8};		int target = 7;		int[] results = sumOfTwo(numbers, target);		logger.info("Index 1: " + results[0] + " index 2: " + results[1]);	}	private static int[] sumOfTwo(int[] numbers, int target) {		Map<Integer, Integer> visitedNumbers = new HashMap<>();		for (int i = 0; i <= numbers.length; i++) {			int delta = target - numbers[i];			if (visitedNumbers.containsKey(delta)) {				return new int[]{i, visitedNumbers.get(delta)};			}			visitedNumbers.put(numbers[i], i);		}		return new int[]{-1, -1};	}}